/** @format */

import React from 'react';
import Header from './Header';
import './ComponentStyle.css';
import { useState, useEffect, useRef } from 'react';

function Slider() {
  const [initialImgClass, changeImageClass] = useState('image-div1');
  const [countDown, setCountDown] = useState();
  const timerId = useRef();

  useEffect(() => {
    timerId.current = setInterval(() => {
      setCountDown((prev) => prev - 1);
    }, 1000);
  });
  const prevButton = () => {
    changeImageClass('image-div2');
  };

  const nextButton = () => {
    changeImageClass('image-div3');
  };
  return (
    <div>
      <Header />
      <div className='container-fluid text-center'>
        <div className='row'>
          <div className='col-sm mt-5'>
            {/* <img className='image-div1 img-fluid' /> */}
            <img className={`img-fluid ${initialImgClass}`} />
          </div>
          <div className='container mt-3'>
            <div
              className='container text-end'
              id='button1'
            >
              <button
                className='btn btn-primary'
                onClick={prevButton}
              >
                <i className=' fa fa-arrow-circle-left'></i> Prev
              </button>
            </div>
            <div
              className='container text-start'
              id='button2'
            >
              <button
                className='btn btn-primary'
                onClick={nextButton}
              >
                Next <i className=' fa fa-arrow-circle-right'></i>
              </button>
            </div>
          </div>
        </div>
        <p>Count Down: {countDown}</p>
      </div>
    </div>
  );
}

export default Slider;
