/** @format */

import React from 'react';
import Header from './Header';
import './ComponentStyle.css';
import { useState, useEffect, useRef } from 'react';

function Slider() {
  const [initialImgClass, changeImageClass] = useState('image-div1');
  const [count, setCount] = useState(50);
  let timer;

  useEffect(() => {
    let progressBar = document.querySelector('#progress-bar');
    let initialProgressBarWidth = 50;
    timer = setInterval(() => {
      setCount(count + 1);
      progressBar.style.width = initialProgressBarWidth + count + 'px';
      if (count === 100 && progressBar.style.width === 100 + 'px') {
        console.log(progressBar.style.width);
        changeImageClass('image-div2');
      } else if (count === 150) {
        progressBar.style.width = initialProgressBarWidth;
        changeImageClass('image-div3');
      } else if (count === 200) {
        progressBar.style.width = initialProgressBarWidth;
        changeImageClass('image-div1');
        setCount(0);
      }
    }, 100);
    return () => clearInterval(timer);
  });

  return (
    <div>
      <Header />
      <div className='container-fluid text-center'>
        <div className='row'>
          <div className='col-sm mt-5'>
            <img className={`img-fluid ${initialImgClass}`} />
          </div>
        </div>
        <div
          className='container text-end'
          id='div-loading'
        >
          <h6
            id='txtCount'
            className='text-success'
          >
            Loading... {count} %
          </h6>
        </div>
      </div>
      <div
        className='container'
        id='div-progress-bar'
      >
        <p id='progress-bar'>...</p>
      </div>
      <div
        className='container-fluid'
        id='div-developer-info'
      >
        <div className='container text-center'>
          <div className='container-fluid mt-1'>
            <p className='bg-primary text-white'>
              Created by Adebayo Bunmi Solomon
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Slider;
