/** @format */

import React from 'react';
import Header from './Header';
import './ComponentStyle.css';
import { useState, useEffect, useRef } from 'react';

function Slider() {
  const [initialImgClass, changeImageClass] = useState('image-div1');
  const [count, setCount] = useState(0);
  let timer;

  useEffect(() => {
    timer = setInterval(() => {
      setCount(count + 1);
      if (count === 10) {
        changeImageClass('image-div2');
      } else if (count === 20) {
        changeImageClass('image-div3');
      } else if (count === 30) {
        changeImageClass('image-div1');
        setCount(0);
      }
    }, 1000);
    return () => clearInterval(timer);
  });

  return (
    <div>
      <Header />
      <div className='container-fluid text-center'>
        <div className='row'>
          <div className='col-sm mt-5'>
            <img className={`img-fluid ${initialImgClass}`} />
          </div>
        </div>
        <div className='container text-start'>
          <p id='txtCount'>{count} %</p>
        </div>
      </div>
      <div className='container-fluid'>
        <div className='container text-center'>
          <div className='container-fluid mt-5'>
            <p className='bg-primary text-white'>By Adebayo Bunmi Solomon</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Slider;
