/** @format */

import React from 'react';
import Header from './Header';
import './ComponentStyle.css';
import { useState, useEffect, useRef } from 'react';

function Slider() {
  const [initialImgClass, changeImageClass] = useState('image-div1');
  //const [minutes, setMinutes] = useState(0);
  //const [seconds, setSeconds] = useState(0);
  let timer;

  // useEffect(() => {
  //   timer = setInterval(() => {
  //     setSeconds(seconds + 1);
  //     if (seconds === 59) {
  //       setMinutes(minutes + 1);
  //       setSeconds(0);
  //     }
  //   }, 1000);
  //   return () => clearInterval(timer);
  // });

  let count = 0;
  const timerFunction = () => {
    count = count + 1;
    const txtCount = document.querySelector('#txtCount');
    txtCount.textContent = count;
    if (count === 10) {
      changeImageClass('image-div2');
    }
  };

  timer = setInterval(() => {
    timerFunction();
  }, 300);

  //For previous button
  const prevButton = () => {
    changeImageClass('image-div2');
  };

  //For next button
  const nextButton = () => {
    changeImageClass('image-div3');
  };
  return (
    <div>
      <Header />
      <div className='container-fluid text-center'>
        <div className='row'>
          <div className='col-sm mt-5'>
            <img className={`img-fluid ${initialImgClass}`} />
          </div>
          <div className='container mt-3'>
            <div
              className='container text-end'
              id='button1'
            >
              <button
                className='btn btn-primary'
                onClick={prevButton}
              >
                <i className=' fa fa-arrow-circle-left'></i> Prev
              </button>
            </div>
            <div
              className='container text-start'
              id='button2'
            >
              <button
                className='btn btn-primary'
                onClick={nextButton}
              >
                Next <i className=' fa fa-arrow-circle-right'></i>
              </button>
            </div>
          </div>
        </div>
        <p id='txtCount'>{/* {minutes}:{seconds} */}</p>
      </div>
    </div>
  );
}

export default Slider;
